#!/bin/bash

###############################################################################
# Daily Calendar Summary - Production Implementation
#
# This script generates a personalized daily calendar summary from Google
# Calendar and sends it to Telegram.
#
# Environment Variables Required:
#   - SECRETS (base64-encoded JSON with GCCLI_CREDS, TELEGRAM_BOT_TOKEN, TELEGRAM_CHAT_ID)
#
# Usage: ./daily_calendar_production.sh
###############################################################################

set -euo pipefail

echo "ğŸš€ Starting Daily Calendar Summary Job"
echo "â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•"

# ============================================================================
# STEP 1: Validate Environment
# ============================================================================

echo -e "\nğŸ“‹ STEP 1: Validating Environment"

if [ -z "${SECRETS:-}" ]; then
  echo "âŒ ERROR: SECRETS environment variable not set"
  exit 1
fi

echo "âœ… SECRETS environment variable detected"

# ============================================================================
# STEP 2: Extract and Decode Credentials
# ============================================================================

echo -e "\nğŸ” STEP 2: Extracting Credentials"

# Decode SECRETS from base64
SECRETS_JSON=$(echo "$SECRETS" | base64 -d 2>&1) || {
  echo "âŒ ERROR: Failed to decode SECRETS from base64"
  exit 1
}

# Validate JSON
echo "$SECRETS_JSON" | jq . > /dev/null 2>&1 || {
  echo "âŒ ERROR: SECRETS is not valid JSON"
  exit 1
}

echo "âœ… SECRETS decoded and validated"

# Extract credentials
GCCLI_CREDS=$(echo "$SECRETS_JSON" | jq -r '.GCCLI_CREDS // empty')
TELEGRAM_BOT_TOKEN=$(echo "$SECRETS_JSON" | jq -r '.TELEGRAM_BOT_TOKEN // empty')
TELEGRAM_CHAT_ID=$(echo "$SECRETS_JSON" | jq -r '.TELEGRAM_CHAT_ID // empty')

# Validate required credentials
if [ -z "$GCCLI_CREDS" ]; then
  echo "âŒ ERROR: GCCLI_CREDS not found in SECRETS"
  exit 1
fi

if [ -z "$TELEGRAM_BOT_TOKEN" ]; then
  echo "âŒ ERROR: TELEGRAM_BOT_TOKEN not found in SECRETS"
  exit 1
fi

if [ -z "$TELEGRAM_CHAT_ID" ]; then
  echo "âŒ ERROR: TELEGRAM_CHAT_ID not found in SECRETS"
  exit 1
fi

echo "âœ… All credentials extracted"

# ============================================================================
# STEP 3: Setup Google Calendar Credentials
# ============================================================================

echo -e "\nğŸ“… STEP 3: Setting Up Google Calendar"

# Extract and setup gccli credentials
echo "$GCCLI_CREDS" | base64 -d 2>&1 | tar xz -C ~ || {
  echo "âŒ ERROR: Failed to extract gccli credentials"
  exit 1
}

# Verify credentials are in place
if [ ! -f ~/.gccli/credentials.json ]; then
  echo "âŒ ERROR: credentials.json not found after extraction"
  exit 1
fi

echo "âœ… Google Calendar credentials configured"

# ============================================================================
# STEP 4: Fetch Calendar Events
# ============================================================================

echo -e "\nğŸ” STEP 4: Fetching Calendar Events"

EMAIL="tilmanbaumann@gmail.com"
TODAY=$(date +%Y-%m-%d)
TOMORROW=$(date -d "tomorrow" +%Y-%m-%d)
START="${TODAY}T00:00:00"
END="${TOMORROW}T00:00:00"

echo "   Querying for: $TODAY"

# Fetch calendars to find IDs
CALENDARS=$(gccli "$EMAIL" calendars 2>/dev/null) || {
  echo "âŒ ERROR: Failed to fetch calendars from Google Calendar"
  echo "   Make sure gccli is installed and configured"
  exit 1
}

# Extract calendar IDs
PRIMARY_EVENTS=$(gccli "$EMAIL" events primary --from "$START" --to "$END" 2>/dev/null || echo "[]")

# Find Family calendar
FAMILY_CAL_ID=$(echo "$CALENDARS" | jq -r '.[] | select(.summary == "Family") | .id' 2>/dev/null || echo "")
if [ -n "$FAMILY_CAL_ID" ]; then
  FAMILY_EVENTS=$(gccli "$EMAIL" events "$FAMILY_CAL_ID" --from "$START" --to "$END" 2>/dev/null || echo "[]")
else
  FAMILY_EVENTS="[]"
  echo "   âš ï¸  Family calendar not found"
fi

# Find VHS calendar
VHS_CAL_ID=$(echo "$CALENDARS" | jq -r '.[] | select(.summary | contains("VHS")) | .id' 2>/dev/null || echo "")
if [ -n "$VHS_CAL_ID" ]; then
  VHS_EVENTS=$(gccli "$EMAIL" events "$VHS_CAL_ID" --from "$START" --to "$END" 2>/dev/null || echo "[]")
else
  VHS_EVENTS="[]"
  echo "   âš ï¸  VHS calendar not found"
fi

# Count events
PRIMARY_COUNT=$(echo "$PRIMARY_EVENTS" | jq 'length')
FAMILY_COUNT=$(echo "$FAMILY_EVENTS" | jq 'length')
VHS_COUNT=$(echo "$VHS_EVENTS" | jq 'length')
TOTAL_COUNT=$((PRIMARY_COUNT + FAMILY_COUNT + VHS_COUNT))

echo "âœ… Events fetched:"
echo "   Primary calendar: $PRIMARY_COUNT events"
echo "   Family calendar: $FAMILY_COUNT events"
echo "   VHS calendar: $VHS_COUNT events"
echo "   Total: $TOTAL_COUNT events"

# ============================================================================
# STEP 5: Process and Format Events
# ============================================================================

echo -e "\nğŸ¨ STEP 5: Formatting Daily Summary"

# Create the Node.js script to format the summary
cat > /tmp/format_summary.js << 'JSEOF'
#!/usr/bin/env node
const fs = require('fs');

// Read events from arguments
const primaryEvents = JSON.parse(process.argv[2] || '[]');
const familyEvents = JSON.parse(process.argv[3] || '[]');
const vhsEvents = JSON.parse(process.argv[4] || '[]');

// Combine and sort by start time
const allEvents = [...primaryEvents, ...familyEvents, ...vhsEvents]
  .filter(e => e.start && (e.start.dateTime || e.start.date))
  .sort((a, b) => {
    const aTime = new Date(a.start.dateTime || a.start.date);
    const bTime = new Date(b.start.dateTime || b.start.date);
    return aTime - bTime;
  });

// Get today's date in German format
const today = new Date();
const todayString = today.toLocaleDateString('de-DE', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// Format time
const formatTime = (dateTimeString) => {
  const dt = new Date(dateTimeString);
  return dt.toLocaleTimeString('de-DE', {
    hour: '2-digit',
    minute: '2-digit',
    hour12: false
  });
};

// Calculate duration
const getDurationMinutes = (start, end) => {
  const s = new Date(start);
  const e = new Date(end);
  return Math.round((e - s) / (1000 * 60));
};

// Get event icon
const getEventIcon = (summary) => {
  const s = summary.toLowerCase();
  if (s.includes('lunch')) return 'ğŸ½ï¸';
  if (s.includes('1:1') || s.includes('meeting') || s.includes('standup')) return 'ğŸ‘¥';
  if (s.includes('review')) return 'âœ…';
  if (s.includes('vhs') || s.includes('course')) return 'ğŸ“š';
  return 'ğŸ¯';
};

// Build summary
let summary = '';
summary += `ğŸŒ… <b>Today's Grand Plan â€“ ${todayString}</b>\n\n`;

if (allEvents.length === 0) {
  summary += `Ah, what a glorious day! You have a completely open calendar. \n`;
  summary += `This is your time to focus on deep work, catch up on tasks, or simply breathe. Make it count! ğŸš€\n`;
} else {
  summary += `Ah, what a wonderful day ahead! You've got a beautiful blend of focused work and meaningful connections lined up. Let's dive into what's waiting for you!\n\n`;

  // Filter timed events
  const timedEvents = allEvents.filter(e => e.start.dateTime);

  if (timedEvents.length > 0) {
    summary += `â° <b>Your Schedule</b>\n`;
    timedEvents.forEach(event => {
      const startTime = formatTime(event.start.dateTime);
      const duration = getDurationMinutes(event.start.dateTime, event.end.dateTime);
      const icon = getEventIcon(event.summary);
      summary += `${icon} <b>${startTime}</b> â€“ ${event.summary} (${duration} min)\n`;
    });
    summary += `\n`;
  }
}

// Family section
if (familyEvents.length > 0 || vhsEvents.length > 0) {
  summary += `ğŸ‘¨â€ğŸ‘©â€ğŸ‘§ <b>For the Family</b>\n`;

  vhsEvents.forEach(event => {
    if (event.start.dateTime) {
      const startTime = formatTime(event.start.dateTime);
      const duration = getDurationMinutes(event.start.dateTime, event.end.dateTime);
      summary += `ğŸ“š <b>${startTime}</b> â€“ ${event.summary}\n`;
      summary += `   Your wife's language learning session! Show some support! ğŸ’ª\n`;
    }
  });

  familyEvents.forEach(event => {
    if (event.start.dateTime) {
      const startTime = formatTime(event.start.dateTime);
      summary += `ğŸ‰ <b>${startTime}</b> â€“ ${event.summary}\n`;
    }
  });

  summary += `\n`;
}

// Motivational closing
summary += `ğŸš€ <b>Your Vibe for Today</b>\n`;

if (allEvents.length === 0) {
  summary += `You've got a blank canvas today. Use it wisely â€“ for deep focus, creative thinking, or strategic planning. You've got this! ğŸ’ª\n`;
} else if (vhsEvents.length > 0 || familyEvents.length > 0) {
  summary += `You've got an excellent balance between focused work and personal connections! `;
  summary += `Your schedule shows ${primaryEvents.length} key work sessions plus quality time with family. `;
  summary += `Your wife's language learning is a great reminder to support her growth. `;
  summary += `Stay present in each meeting, keep the energy up, and make space for those meaningful moments. You've got this! ğŸ’ª\n`;
} else {
  summary += `You've got a solid, well-structured day ahead with ${primaryEvents.length} key sessions. `;
  summary += `The mix of collaboration and focused work will keep your momentum going. Stay sharp and finish strong! ğŸ’ª\n`;
}

summary += `\nMake today count! ğŸ¯`;

console.log(summary);
JSEOF

# Run the formatter
FORMATTED_SUMMARY=$(node /tmp/format_summary.js "$PRIMARY_EVENTS" "$FAMILY_EVENTS" "$VHS_EVENTS")

echo "âœ… Summary formatted"
echo ""
echo "â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•"
echo "ğŸ“§ TELEGRAM MESSAGE:"
echo "â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•"
echo "$FORMATTED_SUMMARY"
echo "â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•"
echo ""

# ============================================================================
# STEP 6: Send to Telegram
# ============================================================================

echo -e "\nğŸ“¤ STEP 6: Sending to Telegram"

# Prepare payload
PAYLOAD=$(cat <<EOF
{
  "chat_id": $TELEGRAM_CHAT_ID,
  "text": $(echo "$FORMATTED_SUMMARY" | jq -Rs .),
  "parse_mode": "HTML"
}
EOF
)

# Send to Telegram
TELEGRAM_RESPONSE=$(curl -s -X POST \
  -H "Content-Type: application/json" \
  -d "$PAYLOAD" \
  "https://api.telegram.org/bot${TELEGRAM_BOT_TOKEN}/sendMessage")

# Check response
SUCCESS=$(echo "$TELEGRAM_RESPONSE" | jq -r '.ok // false')

if [ "$SUCCESS" = "true" ]; then
  MESSAGE_ID=$(echo "$TELEGRAM_RESPONSE" | jq -r '.result.message_id')
  echo "âœ… Message sent successfully!"
  echo "   Message ID: $MESSAGE_ID"
  echo "   Chat ID: $TELEGRAM_CHAT_ID"
else
  ERROR_MSG=$(echo "$TELEGRAM_RESPONSE" | jq -r '.description // "Unknown error"')
  echo "âŒ ERROR: Failed to send Telegram message"
  echo "   Error: $ERROR_MSG"
  exit 1
fi

# ============================================================================
# Completion
# ============================================================================

echo ""
echo "â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•"
echo "âœ… DAILY CALENDAR SUMMARY COMPLETE"
echo "â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•"
echo ""
echo "Summary statistics:"
echo "  - Events processed: $TOTAL_COUNT"
echo "  - Work meetings: $PRIMARY_COUNT"
echo "  - Family events: $FAMILY_COUNT"
echo "  - VHS courses: $VHS_COUNT"
echo "  - Delivery status: âœ… Sent to Telegram"
echo ""
